    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationEmail.Subject"] = [[Verificación en dos pasos con correo electrónico desactivada para la cuenta de Roblox: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRecoveryCodeDepletedEmail.HtmlBody.Under13"] = [[Estimado padre de familia:{lineBreak}{lineBreak}Se han utilizado todos los códigos de recuperación válidos para la cuenta de Roblox de su hijo(a), {accountName}. Para no perder el acceso a la cuenta, puede generar otros códigos nuevos en {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Configuración de la cuenta{aTagEnd}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRecoveryCodeDepletedEmail.HtmlBody.Over13"] = [[Hola, {accountName}:{lineBreak}{lineBreak}Has utilizado todos los códigos de recuperación válidos. Para no perder el acceso a la cuenta, puedes generar códigos adicionales en {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Configuración de la cuenta{aTagEnd}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRecoveryCodeDepletedEmail.Subject"] = [[Ya no hay códigos de recuperación disponibles para la cuenta de Roblox: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobuxSpendEmail.V2.Subject"] = [[Solicitud de verificación para la compra virtual en Roblox de: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobuxSpendEmail.HtmlBody"] = [[{geoLocationInformation}{spanStartTagWithBold}Código para la transacción virtual de {accountName}: {lineBreak}{lineBreak}{code}{spanEndTag}{lineBreak}{lineBreak}Introduce este código en la pantalla de Verificación en dos pasos para finalizar la compra virtual. El código caducará en 15 minutos.{lineBreak}{lineBreak}Te enviamos este correo electrónico debido a actividad sospechosa en la cuenta. Si no realizaste la compra, puede que alguien más esté accediendo a tu cuenta. Te recomendamos que cambies la contraseña si no enviaste esta solicitud.{lineBreak}{lineBreak}Recursos{lineBreak}{aTagStartWithHref}{ChangePasswordLink}{hrefEnd}Cambiar la contraseña{aTagEnd}{lineBreak}{aTagStartWithHref}{TwoStepVerificationArticleLink}{hrefEnd}Aprender más sobre la verificación en dos pasos{aTagEnd}{lineBreak}{aTagStartWithHref}{AccountSafetyArticleLink}{hrefEnd}Cómo proteger la cuenta{aTagEnd}{lineBreak}{aTagStartWithHref}{SupportLink}{hrefEnd}Soporte general de Roblox{aTagEnd} {lineBreak}{lineBreak}Gracias,{lineBreak}{lineBreak}El equipo de Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobuxSpendEmail.PlainBody"] = [[{geoLocationInformation}Código de transacción virtual para {accountName}: {lineBreak}{lineBreak} {code} {lineBreak}{lineBreak}Introduce este código en la pantalla de Verificación en dos pasos para finalizar la compra virtual. El código caducará en 15 minutos. {lineBreak}{lineBreak}Te enviamos este correo electrónico debido a actividad sospechosa en la cuenta. Si no realizaste la compra en Roblox, puede que alguien más esté accediendo a tu cuenta. Te recomendamos que cambies la contraseña si no enviaste esta solicitud. {lineBreak}{lineBreak}Recursos: {lineBreak}Cambiar la contraseña [{accountInfoPageLink}] {lineBreak}Aprender más sobre la verificación en dos pasos [{twoStepVerificationHelpArticleLink}]{lineBreak}Cómo proteger la cuenta [{keepAccountSafeArticleLink}] {lineBreak}Soporte general de Roblox [{supportPageLink}] {lineBreak}{lineBreak}Gracias, {lineBreak}{lineBreak}El equipo de Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobxSpendEmail.V2.Body"] = [[{paragraphStart}Hola,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} es tu código de verificación en dos pasos de Roblox para {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Introduce el código de arriba en la pantalla de Verificación en dos pasos para verificar la transacción virtual. El código caducará en 15 minutos.{paragraphEnd}{paragraphStart}Esta solicitud de inicio de sesión se recibió de {emphasisStart}{geoLocationInformation}{emphasisEnd}.{paragraphEnd}{lineBreak}{paragraphStart}Gracias,{lineBreak}{emphasisStart}El equipo de Roblox{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobuxSpendEmail.V2.Footer"] = [[No respondas a este correo electrónico directamente. Recibiste este correo porque ha habido un aumento en las transacciones virtuales en Roblox. Si no enviaste esta solicitud, te recomendamos que cambies la contraseña.]],
