    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRevertAccountEmail.V1.Body"] = [[{paragraphStart}Bonjour,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} est votre code de vérification en 2 étapes de Roblox pour {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd} {paragraphStart}Entre le code ci-dessus dans l'écran de vérification en 2 étapes pour rétablir les modifications apportées à ton compte. Ce code expirera dans 15 minutes. {paragraphEnd}{paragraphStart} Cette demande de connexion a été reçue de {emphasisStart}{geoLocationInformation}{emphasisEnd}. {paragraphEnd}{lineBreak}{paragraphStart}Merci,{lineBreak}{emphasisStart}L'équipe Roblox{emphasisEnd}{paragraphEnd}]],
