    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRevertAccountEmail.V1.Subject"] = [[Demande de rétablissement du compte Roblox : {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationText.Body.Over13"] = [[Bonjour {accountName},{lineBreak}{lineBreak} Tu as activé la vérification en deux étapes par SMS pour ton compte Roblox. Pour certaines actions comme la connexion, tu dois saisir un code de sécurité à 6 chiffres que Roblox t'enverra par SMS.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationSMS.Body.Under13"] = [[Bonjour,{lineBreak}{lineBreak} La vérification en deux étapes par SMS a été activée pour le compte Roblox de votre enfant, {accountName}. Pour certaines actions comme la connexion, il devra saisir un code de sécurité à 6 chiffres que Roblox vous enverra par SMS.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationText.Body.Under13"] = [[Bonjour,{lineBreak}{lineBreak}La vérification en deux étapes par SMS a été activée pour le compte Roblox de votre enfant, {accountName}. Pour certaines actions comme la connexion, il devra saisir un code de sécurité à 6 chiffres que Roblox vous enverra par SMS.{lineBreak}{lineBreak}Roblox]],
