    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationText.Body.Under13"] = [[Bonjour, {lineBreak}{lineBreak}La vérification en 2 étapes avec les messages texte a été désactivée pour le compte Roblox de ton enfant, {accountName}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationText.Subject"] = [[Vérification en 2 étapes avec messages texte désactivée pour le compte Roblox : {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationText.HtmlBody.Over13"] = [[Bonjour{accountName},{lineBreak}{lineBreak}Ce message a pour but de t'informer que notre équipe d'assistance a désactivé la vérification en deux étapes avec messages texte pour ton compte Roblox. Cela se produit généralement en raison d'un ticket d'assistance que tu as soumis ou par mesure de sécurité pour assurer la sécurité de ton compte. Un code de sécurité ne sera plus nécessaire pour certaines actions comme la connexion. Tu peux réactiver la vérification en deux étapes dans tes {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Paramètres du compte{aTagEnd}.{lineBreak}{lineBreak}Si tu as besoin d'aide, {spanStartTagWithBold}ne réponds pas à cet e-mail{spanEndTag}, et réponds au ticket d'assistance existant ou soumets une nouvelle demande d'assistance ici : {aTagStartWithHref}{supportPageLink}{hrefEnd}Assistance Roblox{aTagEnd}{lineBreak}{lineBreak} Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationText.HtmlBody.Under13"] = [[Bonjour,{lineBreak}{lineBreak}Ce message a pour but de vous informer que notre équipe d'assistance a désactivé la vérification en deux étapes avec messages texte pour le compte Roblox de votre enfant, {accountName}. Cela se produit généralement en raison d'un ticket d'assistance que vous avez soumis ou par mesure de sécurité pour assurer la sécurité de son compte. Un code de sécurité ne sera plus nécessaire pour certaines actions comme la connexion. Vous pouvez réactiver la vérification en deux étapes dans les {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Paramètres du compte{aTagEnd}.{lineBreak}{lineBreak}Si vous avez besoin d'aide, {spanStartTagWithBold}ne répondez pas à cet e-mail{spanEndTag}, et répondez au ticket d'assistance existant ou soumettez une nouvelle demande d'assistance ici : {aTagStartWithHref}{supportPageLink}{hrefEnd}Assistance Roblox{aTagEnd}{lineBreak}{lineBreak} Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Subject"] = [[Changement de vérification en 2 étapes pour {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Body.Over13"] = [[Bonjour,{lineBreak}{lineBreak}Vous avez changé la méthode de vérification en 2 étapes pour {accountName} de {disabledTwoStepMethod} à {enabledTwoStepMethod}. Si vous n'avez pas fait cette demande, contactez l'assistance à la clientèle immédiatement pour récupérer votre compte.{lineBreak}{lineBreak}Merci,{lineBreak}{lineBreak}L'équipe Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Body.Under13"] = [[Bonjour,{lineBreak}{lineBreak}La méthode de vérification en deux étapes pour le compte Roblox de votre enfant {accountName}, a changé de {disabledTwoStepMethod} à {enabledTwoStepMethod}. Si vous ou votre enfant n'avez pas fait cette demande, contactez le service clientèle immédiatement pour récupérer le compte de votre enfant.{lineBreak}{lineBreak}Merci,{lineBreak}{lineBreak}équipe Roblox]],
    ["Authentication.TwoStepVerification.Label.VerifyWithSecurityKey"] = [[Utiliser ta clé de sécurité pour te connecter.]],
