    ["Authentication.TwoStepVerification.Description.TwoStepVerificationAuthenticatorCustomerSupportDeactivationEmail.Subject"] = [[Verifica in due passaggi con autenticatore disattivato dall'assistenza Roblox: {accountName}]],
