    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRobxSpendEmail.V2.Body"] = [[{paragraphStart}Olá,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} é o seu código de verificação em duas etapas da Roblox para {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Insira o código acima na tela de verificação em duas etapas para entrar em sua conta. Este código será invalidado em 15 minutos.{paragraphEnd}{paragraphStart}Esta solicitação de entrada foi recebida de {emphasisStart}{geoLocationInformation}{emphasisEnd}.{paragraphEnd}{lineBreak}{paragraphStart}Atenciosamente,{lineBreak}{emphasisStart}Equipe Roblox{emphasisEnd}{paragraphEnd}]],
